getPlot <- function(x, y, file) {
  myDat <- data.frame(X = x, Y = y) #creating at dataframe
  myP <- ggplot(myDat, aes(x = X, y = Y)) + 
    geom_point() + 
    stat_smooth(method = "lm")
  ggsave(file, plot = myP)
  return(myP) # to get the plot out of the function
}

